import React from 'react';
import { MDBInput } from 'mdbreact';

class  InputPage extends React.Component{



    componentWillMount() {
        this.choicesSentences = this.props.content.choices;
    }
    render() {
        let Checkboxes = this.choicesSentences.map((c, i) => <MDBInput key={i} onChange={this.changedChoiceHandler}
            value={i + 1}>{c}</MDBInput>)
    return (
        <div>
            {/* Material unchecked */}
        {Checkboxes}
        </div>

    )
 }
}

export default InputPage;
